mapper.select:
SELECT * from post
where boardId = #{boardId}
and   postNo = #{postNo}

SELECT * from contents
where boardId = #{boardId}
and   postNo = #{postNo}

mapper.update:
		update post set 
			modDate = sysdate()

			, postTitle = #{postTitle}


    	    , topPostYn = #{topPostYn}


			, dispYn = #{dispYn}


			, replyYn = #{replyYn}


			, delYn = #{delYn}

		where boardId = #{boardId}
		and   postNo = #{postNo}

update post set 
	readCnt = readCnt + 1
where boardId = #{boardId}
and   postNo = #{postNo}

update contents set 
	contents = #{contents}
where boardId = #{boardId}
and   postNo = #{postNo}

mapper.delete:
delete from post 
where boardId = #{boardId}
and   postNo = #{postNo}

delete from contents 
where boardId = #{boardId}
and   postNo = #{postNo}

mapper.update:
update reply set 
	modDate = sysdate()

	,repCont = #{repCont}


	, delYn = #{delYn}

where boardId = #{boardId}
and   postNo  = #{postNo}
and   replyNo = #{replyNo}

mapper.delete:
delete from reply 
where boardId = #{boardId}
and   postNo  = #{postNo}
and   replyNo = #{replyNo}

mapper.select:
SELECT ifnull(max(replyNo),0) 
from reply
where boardId = #{boardId}
and  postNo = #{postNo}

SELECT *
FROM ATTACHFILE
WHERE 1 = 1
and  boardId = #{boardId}
and  postNo = #{postNo}

SELECT *
FROM ATTACHFILE
WHERE 1 = 1
and  boardId = #{boardId}
and  postNo = #{postNo}
and  fileSeq = #{fileSeq}

SELECT ifnull(count(likeNo),0) 
from recommend
where boardId = #{boardId}
and  postNo = #{postNo}
and clCd = 'P'
